"""Virtual Deployment Service STAK library"""from StcIntPythonPL import *import tracebackimport vcmlibfrom utils import checkimport os
def validate(Server, Port, LogFileName, SavePath):    """command validate method returns string"""    logger = PLLogger.GetLogger('spirent.vds')    msg = ''    msg = check(Server, 'Server', msg)    msg = check(Port, 'Port', msg)    msg = check(LogFileName, 'LogFileName', msg)    msg = check(SavePath, 'SavePath', msg)    if len(msg) != 0:        msg = 'get log failed validation' + msg        logger.LogError(msg)        return msg    return ''
def run(Server, Port, LogFileName, SavePath):    """command run method returns True/False"""    logger = PLLogger.GetLogger('spirent.vds')    try:        log_info = vcmlib.getlog(Server, Port, LogFileName)        logger.LogInfo(str(LogFileName))        logger.LogInfo(str(SavePath))        with open(SavePath, 'w') as logfile:            logfile.write(log_info)    except Exception as ex:        if len(ex.message):            msg = str(ex.message)        else:            msg = str(ex)        stack_trace = traceback.format_exc()        logger.LogError('List Images Exception Caught: ' +                        msg + ' ' + stack_trace)        return False    return True

def reset():    """command reset method returns True/False"""    return True
def get_this_cmd():    """returns command handle"""    hnd_reg = CHandleRegistry.Instance()    try:        cmd = hnd_reg.Find(__commandHandle__)    except NameError:        return None    return cmd
